// Setup scene, camera, and renderer
const scene = new THREE.Scene();
const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 10000);
const renderer = new THREE.WebGLRenderer({ antialias: true });
renderer.setSize(window.innerWidth, window.innerHeight);
document.getElementById('container').appendChild(renderer.domElement);

//Ambient light for uniform lighting
const ambientLight = new THREE.AmbientLight(0xffffff, 1); // White light with full intensity
scene.add(ambientLight);



// Create the Sun with texture
const sunGeometry = new THREE.SphereGeometry(0.7, 32, 32); // Size of the Sun
const sunMaterial = new THREE.MeshPhongMaterial({
    map: new THREE.TextureLoader().load('https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT3--QlgSEtJfuxC2uffwKUhl0dwmofWisWmfrreW0WbgThHANTC5b5DcQimg&s')
});
const sun = new THREE.Mesh(sunGeometry, sunMaterial);
scene.add(sun);

// Function to create labels
function createLabel(text) {
    const spriteMaterial = new THREE.SpriteMaterial({
        map: createTextTexture(text),
        depthTest: false
    });
    const sprite = new THREE.Sprite(spriteMaterial);
    sprite.scale.set(2, 1, 1); // Scale the label
    return sprite;
}

// Function to create a texture for text
function createTextTexture(text) {
    const canvas = document.createElement('canvas');
    const context = canvas.getContext('2d');
    context.font = '40px Bree_Serif';
    context.fillStyle = '#FF4F00';
    context.fillText(text, 0, 40);
    const texture = new THREE.Texture(canvas);
    texture.needsUpdate = true;
    return texture;
}

// Create an array of planets with their properties
const planets = [
    { name: "Mercury", radius: 0.1, distance: 0.39, texture: 'data:image/jpeg;base64,', orbitalPeriod: 88 },
    { name: "Venus", radius: 0.2, distance: 0.72, texture: 'data:image/jpeg;base64,', orbitalPeriod: 225 },
    { name: "Earth", radius: 0.2, distance: 1.0, texture: 'data:image/jpeg;base64,', orbitalPeriod: 365.25 },
    { name: "Mars", radius: 0.15, distance: 1.52, texture: 'data:image/jpeg;base64,', orbitalPeriod: 687 },
    { name: "Jupiter", radius: 0.4, distance: 5.2, texture: 'data:image/jpeg;base64,', orbitalPeriod: 4332.59 },
    { name: "Saturn", radius: 0.35, distance: 9.58, texture: 'data:image/jpeg;base64,', orbitalPeriod: 10759.22 },
    { name: "Uranus", radius: 0.25, distance: 19.22, texture: 'data:image/jpeg;base64,', orbitalPeriod: 30687.15 },
    { name: "Neptune", radius: 0.25, distance: 30.05, texture: 'data:image/jpeg;base64,', orbitalPeriod: 60190.03 }
];

const planetMeshes = [];

// Define an offset for the orbits
const orbitOffset = 0.5; // Adjust the distance of the orbits

// Create planets and orbits
planets.forEach(planet => {
    const geometry = new THREE.SphereGeometry(planet.radius, 32, 32);
    const texture = new THREE.TextureLoader().load(planet.texture);
    const material = new THREE.MeshPhongMaterial({ map: texture });

    const mesh = new THREE.Mesh(geometry, material);
    mesh.userData = {
        distance: planet.distance + orbitOffset,
        orbitalSpeed: (2 * Math.PI * (planet.distance + orbitOffset)) / (planet.orbitalPeriod / 365.25) * 0.1
    };
    scene.add(mesh);
    planetMeshes.push(mesh);

    // Create an orbit for the planet
    const orbitGeometry = new THREE.RingGeometry(planet.distance + orbitOffset - 0.01, planet.distance + orbitOffset + 0.01, 64);
    const orbitMaterial = new THREE.MeshBasicMaterial({
        color: 0xffffff,
        side: THREE.DoubleSide,
        transparent: true,
        opacity: 0.7
    });
    const orbit = new THREE.Mesh(orbitGeometry, orbitMaterial);
    orbit.rotation.x = Math.PI / 2;
    scene.add(orbit);

    // Add Saturn's rings with texture
    if (planet.name === "Saturn") {
        const ringInnerRadius = planet.radius * 1.2;
        const ringOuterRadius = planet.radius * 2;
        const ringGeometry = new THREE.RingGeometry(ringInnerRadius, ringOuterRadius, 64);
    
        // Load the texture from the base64 string
        const textureLoader = new THREE.TextureLoader();
        const ringTexture = textureLoader.load('data:image/jpeg;base64,'); 
    
        // Create a material using the loaded texture
        const ringMaterial = new THREE.MeshBasicMaterial({
            map: ringTexture,
            side: THREE.DoubleSide,
            transparent: true,
            opacity: 0.8
        });
    
        // Create the ring mesh with the texture material
        const ring = new THREE.Mesh(ringGeometry, ringMaterial);
        ring.rotation.x = Math.PI / 2; // Align the ring with the planet
        ring.position.set(0, 0, 0);    // Adjust the ring's position to the planet's center
        mesh.add(ring);
    }

    // Add label for the planet
    const label = createLabel(planet.name);
    mesh.add(label);
    label.position.set(0, planet.radius + 0.5, 0);
});

// Create stars in the background
const starGeometry = new THREE.BufferGeometry();
const starCount = 1000;
const positions = new Float32Array(starCount * 3);

for (let i = 0; i < starCount; i++) {
    const distanceFromOrigin = (Math.random() * 30) + 3005;
    positions[i * 3] = (Math.random() - 0.5) * distanceFromOrigin;
    positions[i * 3 + 1] = (Math.random() - 0.5) * distanceFromOrigin;
    positions[i * 3 + 2] = (Math.random() - 0.5) * distanceFromOrigin;
}

starGeometry.setAttribute('position', new THREE.BufferAttribute(positions, 3));
const starMaterial = new THREE.PointsMaterial({ color: 0xffffff, size: 0.2 });
const stars = new THREE.Points(starGeometry, starMaterial);
scene.add(stars);

// Create label for the Sun
const sunLabel = createLabel("Sun");
sun.add(sunLabel);
sunLabel.position.set(0, 1, 0);

// Position the camera
camera.position.set(0, 5, 10);

// Orbit Controls
const controls = new THREE.OrbitControls(camera, renderer.domElement);
controls.enableDamping = true;
controls.dampingFactor = 0.25;
controls.screenSpacePanning = false;
controls.maxPolarAngle = Math.PI / 2;

// Animation loop
function animate() {
    requestAnimationFrame(animate);

    // Rotate planets around the Sun
    planetMeshes.forEach(planet => {
        planet.rotation.y += 0.01;
        planet.position.x = Math.cos(Date.now() * 0.001 * planet.userData.orbitalSpeed) * planet.userData.distance;
        planet.position.z = Math.sin(Date.now() * 0.001 * planet.userData.orbitalSpeed) * planet.userData.distance;
    });

    controls.update();
    renderer.render(scene, camera);
}

