const asteroidMeshes = [];

// Create NEA meshes and add to the scene
nearEarthAsteroids.forEach(asteroid => {
    const geometry = new THREE.SphereGeometry(asteroid.radius, 16, 16);
    const material = new THREE.MeshBasicMaterial({ color: 0x888888 });
    const mesh = new THREE.Mesh(geometry, material);

    // Assign a random inclination between -90 and 90 degrees
    const inclination = THREE.MathUtils.degToRad(Math.random() * 180 - 90);
    mesh.userData = {
        distance: asteroid.distance,
        orbitalSpeed: (2 * Math.PI * asteroid.distance) / asteroid.speed * 0.1,
        inclination: inclination,
        phase: Math.random() * Math.PI * 2 // Random phase for orbital position
    };

    scene.add(mesh);
    asteroidMeshes.push(mesh);

    // Add label for the asteroid
    const label = createLabel(asteroid.name);
    mesh.add(label);
    label.position.set(0, asteroid.radius + 0.2, 0);

    // Create an orbit for the asteroid with a blue color
    const orbitGeometry = new THREE.RingGeometry(asteroid.distance - 0.01, asteroid.distance + 0.01, 64);
    const orbitMaterial = new THREE.MeshBasicMaterial({
        color: 0x0000ff, // Blue color for the orbit
        side: THREE.DoubleSide,
        transparent: true,
        opacity: 0.5
    });
    const orbit = new THREE.Mesh(orbitGeometry, orbitMaterial);
    orbit.rotation.x = mesh.userData.inclination; // Tilt the orbit according to the asteroid's inclination
    scene.add(orbit);
});

// Update asteroid orbits in the animation loop
function animateAsteroids() {
    asteroidMeshes.forEach(asteroid => {
        const time = Date.now() * 0.001 * asteroid.userData.orbitalSpeed;

        // Calculate X and Z positions for circular motion around the Sun
        asteroid.position.x = Math.cos(time + asteroid.userData.phase) * asteroid.userData.distance;
        asteroid.position.z = Math.sin(time + asteroid.userData.phase) * asteroid.userData.distance;

        // Update Y position based on inclination
        asteroid.position.y = Math.sin(asteroid.userData.inclination) * asteroid.userData.distance; // Keep this line

        // Update X and Z positions based on inclination
        asteroid.position.x += Math.sin(asteroid.userData.inclination) * asteroid.userData.distance; 
        asteroid.position.z += Math.cos(asteroid.userData.inclination) * asteroid.userData.distance; 
    });
}

// Call the asteroid animation in the main loop
function animate() {
    requestAnimationFrame(animate);

    // Rotate planets around the Sun
    planetMeshes.forEach(planet => {
        planet.rotation.y += 0.01;
        planet.position.x = Math.cos(Date.now() * 0.001 * planet.userData.orbitalSpeed) * planet.userData.distance;
        planet.position.z = Math.sin(Date.now() * 0.001 * planet.userData.orbitalSpeed) * planet.userData.distance;
    });

    // Animate near-Earth asteroids
    animateAsteroids();

    controls.update();
    renderer.render(scene, camera);
}
animate();



// Load the asteroid texture from a URL
const textureLoader = new THREE.TextureLoader();
const asteroidTexture = textureLoader.load('data:image/jpeg;base64,'); // Replace with the actual URL of the image

// Create an asteroid belt
const asteroidBelt = createAsteroidBelt(500, 3.2, 4.2);
scene.add(asteroidBelt);

// Create an asteroid belt function
function createAsteroidBelt(asteroidCount, minDistance, maxDistance) {
    const asteroidGeometry = new THREE.SphereGeometry(0.02, 16, 16);
    const asteroidMaterial = new THREE.MeshPhongMaterial({
        map: asteroidTexture // Apply the texture loaded from the URL
    });

    const belt = new THREE.Group();

    for (let i = 0; i < asteroidCount; i++) {
        const asteroid = new THREE.Mesh(asteroidGeometry, asteroidMaterial);

        // Randomly position asteroids along the belt (in a ring)
        const distance = Math.random() * (maxDistance - minDistance) + minDistance;
        const angle = Math.random() * 2 * Math.PI;

        asteroid.position.set(
            Math.cos(angle) * distance,
            0, // Keep the asteroids on the plane (y = 0)
            Math.sin(angle) * distance
        );

        // Random rotation for visual diversity
        asteroid.rotation.set(
            Math.random() * Math.PI,
            Math.random() * Math.PI,
            Math.random() * Math.PI
        );

        belt.add(asteroid);
    }

    return belt;
}